import React from "react";
import "../Lounge/Lounge.scss";
import Connect from "../../assets/images/connect-wallet.png";
import Disconnect from "../../assets/images/logout-wallet.png";
import verification from "../../assets/images/verify-wallet.png";
import Toke from "../../assets/images/coin-verified-transparent.gif";
import { useMoralis, useNFTBalances } from "react-moralis";

function Lounge() {
  const {
    logout,
    authenticate,
    isAuthenticated,
    authError,
    user,
    isAuthenticating,
  } = useMoralis();
  const { getNFTBalances, data, error, isLoading, isFetching } =
    useNFTBalances();

  const NFTBalances = () => {
    return (
      <div>
        {error && <>{JSON.stringify(error)}</>}
        <img
          src={verification}
          className="lounge__buttons"
          alt="Verify-wallet"
          onClick={() =>
            getNFTBalances({
              params: {
                token_address: "0x495f947276749Ce646f68AC8c248420045cb7b5e",
              },
            })
          }
        />
      </div>
    );
  };

  if (!isAuthenticated) {
    return (
      <div className="lounge">
        <h1 className="lounge__info">I am Lounge</h1>
        {authError && (
          <p className="access-failure">
            requested signature was not completed.
          </p>
        )}
        {isAuthenticating && <p className="access-failure">loading</p>}
        <img
          src={Connect}
          className="lounge__buttons"
          alt="Connect-Wallet"
          onClick={() => {
            authenticate({
              signingMessage:
                "You are at the right place! Welcome to the Space Bar Lounge! Verify your tokens",
            });
            NFTBalances();
          }}
        />
      </div>
    );
  }

  if (data !== null) {
    if (data.total > 0) {
      return (
        <div className="lounge">
          <h1 className="lounge__info">👀 Welcome {user.get("ethAddress")}</h1>
          <div>
            <p className="lounge__info">congrats, you are VIP</p>
          </div>
          {error && <>{JSON.stringify(error)}</>}
          <img
            src={Toke}
            className="toke"
            onClick={() =>
              getNFTBalances({
                params: {
                  token_address: "0x495f947276749Ce646f68AC8c248420045cb7b5e",
                },
              })
            }
          />
          <pre className="lounge__info">{JSON.stringify(data, null, 2)}</pre>
          <img
            src={Disconnect}
            className="lounge__buttons"
            alt="Logout-wallet"
            onClick={() => logout()}
          />
        </div>
      );
    }
  }

  // This is the case where we have the user's address
  // which means they've connected their wallet to our site!
  return (
    <div className="lounge">
      <h1 className="lounge__info">
        👀 Welcome {user.get("ethAddress")} You Don't Have NFT
      </h1>
      <NFTBalances />
      <img
        src={Disconnect}
        className="lounge__buttons"
        alt="signing-off"
        onClick={() => logout()}
      />
    </div>
  );
}

