import React from "react";
import "../Lounge/Lounge.scss";
import Connect from "../../assets/images/connect-wallet.png";
import Disconnect from "../../assets/images/logout-wallet.png";
import { useMoralis } from "react-moralis";

const LogoutButton = () => {
  const { logout, isAuthenticating } = useMoralis();

  return (
    <img
      src={Disconnect}
      className="connect-bttn"
      alt="Connect-Wallet"
      isLoading={isAuthenticating}
      onClick={() => logout()}
      disabled={isAuthenticating}
    />
  );
};

function Lounge() {
  const { authenticate, isAuthenticated, user, authError } = useMoralis({
    address: "0x495f947276749ce646f68ac8c248420045cb7b5e",
  });
  if (!isAuthenticated) {
    return (
      <div>
        <img
          src={Connect}
          className="connect-bttn"
          alt="Connect-Wallet"
          onClick={() =>
            authenticate({
              signingMessage:
                "You are at the right place! Welcome to the Space Bar Lounge! Verify your tokens",
            })
          }
        />
        else{" "}
        {
          <div>
            HexAbout
            {authError && (
              <Alert status="failed">
                <AlertIcon />
                <Box flex="1">
                  <AlertTitle>Authentication Failed</AlertTitle>
                  <AlertDescription display="block">
                    {authError.message}
                  </AlertDescription>
                </Box>
                <CloseButton position="absolute" right="8px" top="8px" />
              </Alert>
            )}
          </div>
        }
        <div className="lounge">
          <p className="lounge__info">I am Lounge</p>
        </div>
      </div>
    );
  }

  return (
    <div>
      <LogoutButton />
      <h1>Welcome {user.get("username")}, to the Space Bar Lounge!</h1>
    </div>
  );
}

export default Lounge;
