import React from "react";
import ReactCardFlip from "react-card-flip";
// import Front from "../../assets/images/pink-sauce-nft-first-edition.png";
// import Back from "../../assets/images/pink-sauce-nft-first-edition-rear.png";
// import "../Card/Card.scss";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      isFlipped: false,
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e) {
    e.preventDefault();
    this.setState((prevState) => ({ isFlipped: !prevState.isFlipped }));
  }

  render() {
    return (
      <ReactCardFlip isFlipped={this.state.isFlipped} flipDirection="vertical">
        <YOUR_FRONT_CCOMPONENT>
          <img src={Front} className="front" alt="nft" />
          <button onClick={this.handleClick}>Click to flip</button>
        </YOUR_FRONT_CCOMPONENT>

        <YOUR_BACK_COMPONENT>
          <img src={Back} className="back" alt="nft" />
          <button onClick={this.handleClick}>Click to flip</button>
        </YOUR_BACK_COMPONENT>
      </ReactCardFlip>
    );
  }
}

export default App;
